<template lang="html">
  <el-button type="text" class="full-screen" @click="handleClick">
    <i v-if="isFullscreen" class="fontscreen fullscreen-exit"></i>
    <i v-else class="fontscreen fullscreen"></i>
  </el-button>
</template>

<script>
import screenfull from 'screenfull'
export default {
  name: 'ScreenFull',
  data() {
    return {
      isFullscreen: false
    }
  },
  mounted() {
    this.init()
  },
  methods: {
    handleClick() {
      if (!screenfull.enabled) {
        this.$message({
          message: 'you browser can not work',
          type: 'warning'
        })
        return false
      }
      screenfull.toggle()
    },
    init() {
      if (screenfull.enabled) {
        screenfull.on('change', () => {
          this.isFullscreen = screenfull.isFullscreen
        })
      }
    }
  }
}
</script>

